// Normalize data from different platforms into unified format
const items = [];

for (const item of $input.all()) {
  const platform = item.json.platform;
  let normalized = {
    streamer_id: item.json.streamer_id,
    platform: platform,
    platform_user_id: item.json.platform_user_id,
    handle: item.json.handle,
    is_live: false,
    viewer_count: 0,
    stream_title: null,
    stream_thumbnail: null,
    stream_url: null,
    content_items: [],
    raw_response: item.json
  };

  // Parse based on platform
  if (platform === 'TWITCH' && item.json.data && item.json.data.length > 0) {
    const stream = item.json.data[0];
    normalized.is_live = true;
    normalized.viewer_count = stream.viewer_count || 0;
    normalized.stream_title = stream.title;
    normalized.stream_thumbnail = stream.thumbnail_url;
    normalized.stream_url = `https://twitch.tv/${item.json.handle}`;
  } else if (platform === 'YOUTUBE' && item.json.items && item.json.items.length > 0) {
    const video = item.json.items[0];
    normalized.is_live = true;
    normalized.stream_title = video.snippet.title;
    normalized.stream_thumbnail = video.snippet.thumbnails.high.url;
    normalized.stream_url = `https://youtube.com/watch?v=${video.id.videoId}`;
  } else if (platform === 'KICK' && item.json.livestream) {
    normalized.is_live = item.json.livestream.is_live || false;
    normalized.viewer_count = item.json.livestream.viewer_count || 0;
    normalized.stream_title = item.json.livestream.session_title;
    normalized.stream_thumbnail = item.json.livestream.thumbnail.url;
    normalized.stream_url = `https://kick.com/${item.json.handle}`;
  }

  items.push({ json: normalized });
}

return items;

// Merge AI insights back into stream data
const items = [];

for (let i = 0; i < $input.all().length; i++) {
  const streamData = $node["Normalize Platform Data"].json[i];
  const aiAnalysis = JSON.parse($input.all()[i].json.choices[0].message.content);
  const embedding = $input.all()[i].json.data[0].embedding;

  items.push({
    json: {
      ...streamData,
      ai_category: aiAnalysis.category,
      ai_mood: aiAnalysis.mood,
      ai_target_audience: aiAnalysis.target_audience,
      ai_content_rating: aiAnalysis.content_rating,
      ai_topics: aiAnalysis.key_topics,
      ai_sentiment: aiAnalysis.sentiment,
      embedding_vector: embedding,
      enriched_at: new Date().toISOString()
    }
  });
}

return items;

// Build personalized notification payload
const items = [];

for (let i = 0; i < $input.all().length; i++) {
  const user = $node["Get Followers with Preferences"].json[i];
  const timing = $node["AI: Predict Best Send Time"].json[i];
  const streamData = $node["Store Enriched Stream Data"].json;
  const similarStreams = $node["AI: Find Similar Live Streams"].json;

  const shouldSendImmediately = timing.send_immediately && timing.predicted_open_rate > 0.25;

  items.push({
    json: {
      user_id: user.user_id,
      user_name: user.display_name,
      user_email: user.email,
      push_token: user.push_token,
      push_platform: user.push_platform,
      send_immediately: shouldSendImmediately,
      optimal_send_time: timing.optimal_send_time,
      predicted_open_rate: timing.predicted_open_rate,
      notification_payload: {
        title: `üî¥ ${streamData.handle} is now live!`,
        body: `${streamData.stream_title} ‚Ä¢ ${streamData.ai_category} ‚Ä¢ ${streamData.viewer_count} viewers`,
        data: {
          type: 'stream_live',
          streamer_id: streamData.streamer_id,
          stream_url: streamData.stream_url,
          category: streamData.ai_category,
          mood: streamData.ai_mood,
          similar_streams: similarStreams.slice(0, 3).map(s => s.id)
        }
      },
      email_payload: {
        subject: `${streamData.handle} is streaming ${streamData.ai_category}!`,
        html: `<h1>üî¥ ${streamData.handle} just went live!</h1><p><strong>${streamData.stream_title}</strong></p><p>Category: ${streamData.ai_category} ‚Ä¢ Mood: ${streamData.ai_mood}</p><p><a href="${streamData.stream_url}">Watch Now</a></p>${similarStreams.length > 0 ? '<h3>You might also like:</h3><ul>' + similarStreams.map(s => `<li>${s.title}</li>`).join('') + '</ul>' : ''}`
      }
    }
  });
}

return items;

// Format chat response for mobile app
const aiResponse = $json.choices[0].message.content;
const conversationId = $node["AI Chat Assistant Webhook"].json.body.conversation_id || $execution.id;

return [{
  json: {
    conversation_id: conversationId,
    message: aiResponse,
    timestamp: new Date().toISOString(),
    metadata: {
      model: 'gpt-4',
      function_used: $node["GPT-4 Chat Response"].json.choices[0].message.function_call?.name || null,
      tokens_used: $json.usage.total_tokens
    }
  }
}];
